import React, { useState, useEffect } from "react";
import { api } from "../../shared/api/todos/index";
import { useTodos } from "../../shared/hooks/index";


export const Cart: React.FC = () => {

    const { todos, addTodo, deleteTodo, toggleTodo } = useTodos(); // Вызов хука

    useEffect(() => {
        const fetchTodos = async () => {
            try {
                const response = await api.getTodos();
                console.log(response)

                if (response.title) {
                    addTodo(response.id, response.title, response.completed)
                }
            } catch (error) {
                console.error("Ошибка при получении задач:", error);
            }
        };

        fetchTodos();
    }, []);

    const handleDelente = async (id: number) => {
        try {
            await api.deleteTodo(id);
            deleteTodo(id)
        } catch (error) {
            console.error("Ошибка при удалении задачи: ", error);
        }
    }


    return (
        <div>
            {todos.map((todo) => {
                return (
                    <div key={todo.id} className="cart">
                        <input type="checkbox" checked={todo.completed}
                            onChange={() => toggleTodo(todo.id)} />
                        <h2>{todo.title}</h2>
                        <button onClick={() => handleDelente(todo.id)}>удалить</button>
                    </div>
                )
            })}
        </div>
    );

}
