import React, { useState, useEffect } from "react";
import { useTodos } from "../../shared/hooks/index";
import { api } from "../../shared/api/todos/index"; // Импортируем api
import "../../style.css"
export const AddCart: React.FC = () => {
    const { todos, addTodo } = useTodos(); // Вызов хука
    const [text, setText] = useState(""); // Используем строку для состояния

    const handleAddTodo = async () => {
        if (text.trim()) { // Проверяем, что текст не пустой
            const newTodo = {
                id: Date.now(), // Генерация уникального ID
                title: text,
                completed: false,
            };
            await api.postCreateTodo(newTodo); // Отправляем новую задачу на сервер
            addTodo(newTodo.id, newTodo.title, newTodo.completed); // Добавляем задачу в локальное состояние
            setText(""); // Очищаем поле ввода после добавления
        }
    };

    useEffect(() => {
        const fetchTodos = async () => {
            try {
                const response = await api.getTodos();
                console.log(response);

                if (Array.isArray(response)) {
                    response.forEach(todo => {
                        // Проверяем, существует ли задача уже в состоянии
                        if (!todos.some(existingTodo => existingTodo.id === todo.id)) {
                            addTodo(todo.id, todo.title, todo.completed);
                        }
                    });
                }
            } catch (error) {
                console.error("Ошибка при получении задач:", error);
            }
        };

        fetchTodos();
    }, [handleAddTodo]); // Убираем todos из зависимостей

    return (
        <div className="containerAdd">
            <input
                className="AddInput"
                type="text"
                value={text}
                onChange={(event) => setText(event.target.value)} // Устанавливаем текст
            />
            <button className="AddButton" onClick={handleAddTodo}>Добавить</button>
        </div>
    );
};
