import React, { useState } from "react";


interface Todo {
    id: number;
    title: string;
    completed: boolean;
}

export const useTodos = () => { // Убрали React.FC
    const [todos, setTodos] = useState<Todo[]>([]); // Исправлено на круглые скобки

    const addTodo = (id: string, title: string, completed: boolean) => {
        const newTodo: Todo = {
            id,
            title,
            completed,
        };

        setTodos([...todos, newTodo]);
    };

    const toggleTodo = (id: number) => {
        setTodos(todos.map(todo => 
            todo.id === id ? { ...todo, completed: !todo.completed } : todo
        ));
    };

    const deleteTodo = (id: number) => { // Исправлено: добавлен => 
        setTodos(todos.filter(todo => todo.id !== id));
    };

    return {
        todos,
        addTodo,
        deleteTodo,
        toggleTodo,
    };
};
