import React, { useEffect, useState } from 'react';
import { useTodos } from '../../shared/hooks/index';
import { api } from '../../shared/api/todos';

export const Selected: React.FC = () => {
    const { toggleTodo, deleteTodo } = useTodos(); // Вызов хука
    const [selectedTodos, setSelectedTodos] = useState<any[]>([]); // Состояние для хранения выбранных задач

    const fetchTodos = async () => {
        try {
            const response = await api.getSelected();
            setSelectedTodos(response); // Сохраняем полученные задачи в состоянии
        } catch (error) {
            console.error('Ошибка при получении списка todos:', error);
        }
    };

    useEffect(() => {
        fetchTodos();
    }, []); // Запускаем только один раз при монтировании компонента

    const handleDelete = async (id: string) => {
        try {
            await api.deleteTodo(id); // Удаляем задачу через API
            deleteTodo(id); // Удаляем задачу из локального состояния
            setSelectedTodos((prev) => prev.filter(todo => todo.id !== id)); // Обновляем состояние, удаляя задачу
        } catch (error) {
            console.error("Ошибка при удалении задачи:", error);
        }
    };

    return (
        <div>
            {selectedTodos.map((todo) => {
                return (
                    <div
                        className="Cart"
                        style={{ background: "green", color: "white" }}
                        key={todo.id} // Используйте уникальный идентификатор todo.id в качестве ключа
                    >
                        <input
                            className="CartInput"
                            type="checkbox"
                            checked={todo.completed}
                            onChange={() => toggleTodo(todo.id)}
                        />
                        <h2 className="CartTitle">{todo.title}</h2>
                        <button className="CartButton" onClick={() => handleDelete(todo.id)}>удалить</button>
                    </div>
                );
            })}
        </div>
    );
};
