import React, { useEffect } from "react";
import { api } from "../../shared/api/todos/index";
import { useTodos } from "../../shared/hooks/index";
import "../../../"

export const Cart: React.FC = () => {
    const { todos, addTodo, deleteTodo, toggleTodo } = useTodos(); // Вызов хука

    useEffect(() => {
        const fetchTodos = async () => {
            try {
                const response = await api.getTodos();
                console.log(response);

                if (Array.isArray(response)) {
                    response.forEach(todo => {
                        // Проверяем, существует ли задача уже в состоянии
                        if (!todos.some(existingTodo => existingTodo.id === todo.id)) {
                            addTodo(todo.id, todo.title, todo.completed);
                        }
                    });
                }
            } catch (error) {
                console.error("Ошибка при получении задач:", error);
            }
        };

        fetchTodos();
    }, [todos]); // Убираем todos из зависимостей

    const handleDelete = async (id: number) => {
        try {
            await api.deleteTodo(id);
            deleteTodo(id);
        } catch (error) {
            console.error("Ошибка при удалении задачи: ", error);
        }
    };

    return (
        <div>
            {todos.map((todo) => {
                return (
                    <div className = "Cart" style={todo.completed ? {background: "green"} : {}} key={todo.id}>
                        <input
                             className = "CartInput"
                            type="checkbox"
                            checked={todo.completed}
                            onChange={() => toggleTodo(todo.id)}
                        />
                        <h2  className = "CartTitle">{todo.title}</h2>
                        <button  className = "CartButton" onClick={() => handleDelete(todo.id)}>удалить</button>
                    </div>
                );
            })}
        </div>
    );
};
